// Inicializacion de los estados a manejar
const initialState = {
    selectedImages: []
}; 
//metodo anonimo que se carga al inicializar el DOM y valida los actions para ejecutar la acción
// correspondiente según la interaccio del usuario con la página
// eslint-disable-next-line import/no-anonymous-default-export
const uploadImage = (state = [], action) => {

    switch (action.type) {
        case 'SELECCCIONAR_ARCHIVOS':
          return [
            ...state,
            action.payload.target.files
          ]
        default:
            return state;
    }
};

export default uploadImage;
