import React from "react";
import { connect } from "react-redux";
import UploadService from "../services/fileUpload";
import ResizeImageService from "../services/resizeImage";

class ImagesList extends React.Component {
    
    constructor(props) {
        super(props);
               
        //seteamos el estado inicial 
        this.state = {
            showAlertMessage: false,
            alertMessage: "",
            showResultMessage: false,
            resultMessageSize: "",
            resultMessageOrientation: "",
            selectedFiles: 0,
            progressBar: [],
            imagesList: [],
            selectedImages: []
        };
        //tamaño de hoja A4 (796 x 1123 pixeles)
        this.widthA4 = 796; //horizontal
        this.heightA4 = 1123; //vertical
        this.imageTypeAccepted = "image/jpeg";

        this.selectedImages = [];

    }

    //metodo que evalua si todos las imagenes seleccionadas son de tipo JPG
    imageTypeValidation = () => {

        let files = this.selectedImages;

        for (let file of files) {
            if(file.type.toLowerCase() !== this.imageTypeAccepted)
                return false;
        }

        return true;
    }
    //se lanza el action de UPLOAD_IMAGES
    /*handleUploadImages = () => {

        const selectedFiles = this.selectedImages
        if(selectedFiles.length > 0){

            if( this.imageTypeValidation() ){
                // actualizamos valor de estado del mensaje de alerta para que se renderice el DOM 
                this.setState((state) => { 
                    return {
                        showAlertMessage: false,
                        alertMessage: ""
                    }
                }) 

                let _progressBar = [];
                for (let selectedFile of selectedFiles) {
                    _progressBar.push({ percentage: 0, fileName: selectedFile.name });
                }

                this.setState(
                {
                    progressBar: _progressBar,
                },
                () => {
                    for (let i = 0; i < selectedFiles.length; i++) {
                    this.upload(i, selectedFiles[i]);
                    }
                });
            } else {
                this.setState((state) => { 
                    return {
                        showAlertMessage: true,
                        alertMessage: "Solo se permiten imagenes tipo "+this.imageTypeAccepted,
                        showResultMessage: false                    
                    }
                }) 
            }

        } else {

            this.setState((state) => { 
                return {
                    showAlertMessage: true,
                    alertMessage: "No ha seleccionado ningún archivo aún!",
                    showResultMessage: false                    
                }
            }) 
        }
    }
    //metodo que haciendo uso del servicios de Axios 
    //lanza la petición web para al API que se encarga de subir los archivos 
    //al directorio local
    upload(idx, file) {
   
        let _progressBar = [...this.state.progressBar];
    
        UploadService.upload(file, (event) => {
          _progressBar[idx].percentage = Math.round((100 * event.loaded) / event.total);
          this.setState({
            progressBar: _progressBar,
          });
        })
        .then(() => {
            this.setState((prev) => {
                let nextMessage = "Imagenes cargadas exitosamente"
                return {
                    showAlertMessage: true,
                    alertMessage: nextMessage,
                    selectedImages: []
                };
            });

            return UploadService.getFiles()
            .then((files) => {
                this.setState({
                    imagesList: files.data,
                });
            })
        })
        .catch(() => {
        _progressBar[idx].percentage = 0;
            this.setState((prev) => {
                let nextMessage = prev.alertMessage+"No se pudo cargar la imagen: " + file.name+". ";
                return {
                    progressBar: _progressBar,
                    showAlertMessage: true,
                    alertMessage: nextMessage,
                    showResultMessage: false,
                
                };
            });
        });
        
    }

    //se lanza el action de SELECT_FILES
    handleChange(event) {
        this.selectedImages = event.target.files;
        console.log(this.selectedImages)
        this.props.selectFiles(event)
    }


    //metodo que implementa el algoritmo de redimension de la imagen
    resizeItem (width, heigth) { 

        let res = ResizeImageService.resize(this.widthA4,this.heightA4,width,heigth)
        let msg = res[0];

        let newWidth = res[2];
        let newHeight = res[3];

        let orientation = ResizeImageService.orientation(newWidth,newHeight)
        
        // actualizamos valor de estado del mensaje de alerta para que se renderice el DOM 
        this.setState((state) => { 
            return {
                showAlertMessage: false,
                showResultMessage: true,
                resultMessageSize: msg,
                resultMessageOrientation: orientation,
                progressBar: []
            }
        })

    }

   
*/
 //metodo que renderiza mensaje en pantalla si el estado de showAlertMessage cambia
    renderAlertMessage(){
        return this.state.showAlertMessage ?  
        <div className="alert alert-danger" role="alert">{this.state.alertMessage}</div> : null
    }

    //metodo que renderiza mensaje en pantalla si el estado de showResultMessage cambia
    renderResultMessage(){
        return this.state.showResultMessage ?  

        <div className="alert alert-success" role="alert">
            <p>{this.state.resultMessageSize}</p>
            <p>{this.state.resultMessageOrientation}</p>
        </div> : null
    }
    //metodo que se llama una vez se ha cargado el componente
    //al setear el estado aqui, cada vez que se modifique se volvera a mostrar en el DOM
    componentDidMount() {

        UploadService.getFiles().then((response) => {
            this.setState({
                imagesList: response.data,
            });
        });
    }


    
    //metodo que renderiza todo el contenido del componente en el DOM la primera vez que se carga la página
    render() {
        return (
            <div>
                
                {this.state.progressBar &&
                this.state.progressBar.map((progressInfo, index) => (
                    <div className="mb-2" key={index}>
                    <span>{progressInfo.fileName}</span>
                    <div className="progress">
                        <div
                        className="progress-bar progress-bar-info"
                        role="progressbar"
                        aria-valuenow={progressInfo.percentage}
                        aria-valuemin="0"
                        aria-valuemax="100"
                        style={{ width: progressInfo.percentage + "%" }}
                        >
                        {progressInfo.percentage}%
                        </div>
                    </div>
                    </div>
                ))}

                {this.renderAlertMessage()}
                {this.renderResultMessage()}
                
                
                { this.state.imagesList &&(
                <div className="card mt-3">
                    <div className="card-header">Lista de Imagenes</div>
                    <ul className="list-group list-group-flush">
                       
                            {this.state.imagesList.map((img, index) => (
                        
                                <li className="list-group-item" key={index}>
                                <p><a href={img.url} target="_blank" rel="noreferrer">Ver {img.name}</a></p>
                                <p>{"Tamaño original (ancho x alto):"+img.width+"px X "+img.height+"px"}</p>
                                <img src={img.url} alt={img.name} height="100px"  />
                                <div className="content-detail-info"> 
                                    <button className="btn btn-info btn-sm"
                                    onClick={()=>this.resizeItem(img.width, img.height)}>Redimensionar</button> 
                                    </div> 
                                   
                                </li>
                            ))}
                        
             
                    </ul>
                </div>
                )}

            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
      selectedFiles: state.selectedFiles
    }
}


export default connect(mapStateToProps, {})(ImagesList);
