import React from "react";
import { connect } from "react-redux";

//const ImagesList = ({ proccess }) => (
class ImagesList extends React.Component {
    
        constructor(props) {
            super(props);
                   
            //seteamos el estado inicial 
            this.state = {
                showAlertMessage: false,
                alertMessage: "",
                showResultMessage: false,
                resultMessageSize: "",
                resultMessageOrientation: "",
                selectedFiles: 0,
                progressBar: [],
                imagesList: [],
                selectedImages: []
            };
            //tamaño de hoja A4 (796 x 1123 pixeles)
            this.widthA4 = 796; //horizontal
            this.heightA4 = 1123; //vertical
    
            this.selectedImages = [];
    
        }
    
    
    render (){

        return (
            <div>
                {this.state.showAlertMessage ? <div className="alert alert-danger" role="alert">{this.state.alertMessage}</div> : null}
                
            </div>
        )
    }   
    
}

const mapStateToProps = (state) => {
    return {
        state
    }
}


export default connect(mapStateToProps)(ImagesList);
