const ProgressBarFiles = ({ uploadImage, onTodoClick }) => (
    <div>
       <h1>prueba mensaje:</h1>
       {uploadImage.map((progressInfo) => (
                <div>{progressInfo.alertMessage}</div>
       ))}
    </div>
  )

const mapStateToProps = (state) => {
    console.log("mapStateToProps")
    console.log(state)
    return {
        uploadImage : state
    }
}

const mapDispatchToProps = (dispatch) => {

    return {
      onTodoClick: (id) => {
        dispatch(upload(id))
      }
    }
  }
  
const ProgressBar = connect(
    mapStateToProps,
    mapDispatchToProps
)(ProgressBarFiles)
  
  export default ProgressBar;
