const initialState = {
    showAlertMessage: false,
    alertMessage: "",
    showResultMessage: false,
    resultMessageSize: "",
    resultMessageOrientation: "",
    selectedFiles: 0,
    progressBar: [],
    imagesList: [],
    selectedImages: []
}

const images = (state = {}, action) => {
    switch (action.type) {
      case 'ADD_TODO':
        return {
          id: action.id,
          text: action.text,
          completed: false
        }
      case 'TOGGLE_TODO':
        if (state.id !== action.id) {
          return state
        }
  
        return Object.assign({}, state, {
          completed: !state.completed
        })
  
      default:
        return state
    }
  }

