export const selectFiles = (evento) => {
    console.log("selectFiles")
    console.log(evento.target.files)
    return {
        type: 'SELECCCIONAR_ARCHIVOS',
        files: evento.target.files
    }
}

export const uploadImages = evento =>{
    return {
        type: 'CARGAR_ARCHIVOS',
        payload: evento
    }
}
