import React from "react";
import { connect } from 'react-redux'
import {upload} from "../actions/upload"

class ProgressBarFiles extends React.Component {
    
    constructor(props) {
        super(props);
               
        //seteamos el estado inicial 
        this.state = {
            showAlertMessage: false,
            alertMessage: "",
            showResultMessage: false,
            resultMessageSize: "",
            resultMessageOrientation: "",
            selectedFiles: 0,
            progressBar: [],
            imagesList: [],
            selectedImages: []
        };
    }

    render() {
        console.log("entra a render alertMessage="+this.state.alertMessage)

        return (
    
            (this.state.progressBar && this.state.progressBar.map((progressInfo, index) => (
                <div className="mb-2" key={index}>
                <span>{progressInfo.fileName}</span>
                <div className="progress">
                    <div
                    className="progress-bar progress-bar-info"
                    role="progressbar"
                    aria-valuenow={progressInfo.percentage}
                    aria-valuemin="0"
                    aria-valuemax="100"
                    style={{ width: progressInfo.percentage + "%" }}
                    >
                    {progressInfo.percentage}%
                    </div>
                </div>
                </div>
            )))
        )
    }
}

const mapStateToProps = (state) => {
    console.log("mapStateToProps")
    console.log(state)
    return {
        alertMessage : state.alertMessage,
        progressBar: state.progressBar
    }
}

const mapDispatchToProps = (dispatch) => {

    return {
      onTodoClick: (id) => {
        dispatch(upload(id))
      }
    }
  }
  
const ProgressBar = connect(
    mapStateToProps,
    mapDispatchToProps
)(ProgressBarFiles)
  
  export default ProgressBar;
