const initialState = {
    showAlertMessage: false,
    alertMessage: "",
    showResultMessage: false,
    resultMessageSize: "",
    resultMessageOrientation: "",
    selectedFiles: 0,
    progressBar: [],
    imagesList: [],
    selectedImages: []
}

//metodo que valida los actions para ejecutar la acción
// correspondiente según la interaccion del usuario con la página
const uploadImage = (state = initialState, action) => {

    switch (action.type) {
        case 'SELECCCIONAR_ARCHIVOS':
            console.log("SELECCCIONAR_ARCHIVOS ")
            console.log(action.files)
            let selectedFiles = action.files;
            let files = []
            for (let selectedFile of selectedFiles) {
                files.push({ name: selectedFile.name , type : selectedFile.type});
            }
            console.log(files)
            console.log("despues files")

          return {
            ...state,
            selectedImages: action.files
          }
        default:
            return state;
    }
};

export default uploadImage;
