export const selectFiles = (evento) => {
    return {
        type: 'SELECCCIONAR_ARCHIVOS',
        files: evento.target.files
    }
}

const imageTypeAccepted = "image/jpeg";
export const uploadImages = () =>{
    return function(dispatch) {
        
        return {
            type: 'LISTA_IMAGENES', 
            imgType: imageTypeAccepted
        }
    }
}

export const upload = () =>{
    return {
        type: 'CARGAR'
    }
}
