import React from "react";
import { connect } from "react-redux";

class ImagesList extends React.Component {
    
    constructor(props) {
        super(props);
               
        //tama침o de hoja A4 (796 x 1123 pixeles)
        this.widthA4 = 796; //horizontal
        this.heightA4 = 1123; //vertical

    }

    //metodo que renderiza todo el contenido del componente en el DOM la primera vez que se carga la p치gina
    render() {
        return (
            <div>
            (this.props.showAlertMessage ?   <div className="alert alert-danger" role="alert">{this.props.alertMessage}</div> : null)

            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        alertMessage: state.alertMessage
    }
}


export default connect(mapStateToProps)(ImagesList);
