const initialState = {
    showAlertMessage: false,
    alertMessage: "",
    showResultMessage: false,
    resultMessageSize: "",
    resultMessageOrientation: "",
    selectedFiles: [],
    progressBar: [],
    imagesList: [],
    selectedImages: []
}

const image = (state = {}, action) => {
    switch (action.type) {
      case 'SELECCCIONAR_ARCHIVOS':
        return {
          selectedImages: action.payload.target.files
        }
  
      default:
        return state
    }
  }

//metodo que valida los actions para ejecutar la acción
// correspondiente según la interaccion del usuario con la página
const uploadImage = (state = initialState, action) => {

    switch (action.type) {
        case 'SELECCCIONAR_ARCHIVOS':
          return [
            ...state,
            image(action.payload.target.files)
          ]
        default:
            return state;
    }
};

export default uploadImage;
